// Step3
import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Form, Button, Select, message as Message } from 'antd';
import firebase from '../../Config/firebase';

const FormItem = Form.Item;
const { Option } = Select;
const Users = firebase.firestore().collection('Users');

class Step3 extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loading: false,
    };
  }

  componentDidMount() {
    const { history } = this.props;

    const status = localStorage.getItem('status');
    if (status !== 'step2') history.push('/home');
  }

  submitStep = e => {
    e.preventDefault();
    this.setState({ loading: true });
    const {
      form: { validateFields },
    } = this.props;
    validateFields((err, values) => {
      const { beverages, duration } = values;
      if (!err) {
        Users.doc(localStorage.getItem('uid'))
          .update({ beverages, duration, status: 'step3' })
          .then(() => {
            const { history } = this.props;
            this.setState({ loading: false });
            localStorage.setItem('status', 'step3');
            history.push('/profile/step4');
          })
          .catch(error => {
            Message.error('Something Went Wrong! See console for log.');
            console.log('ERROR => ', error);
          });
      } else {
        this.setState({ loading: false });
      }
    });
  };

  render() {
    const {
      form: { getFieldDecorator },
    } = this.props;
    const { loading } = this.state;

    return (
      <div className="section">
        <h2>Step 03</h2>
        <p>Some more things to find you a better match!</p>

        <Form onSubmit={this.submitStep} className="login-form">
          <FormItem style={{ marginBottom: 10 }} label="Select Beverages">
            {getFieldDecorator('beverages', {
              rules: [{ message: 'Please Select Beverages!', required: true }],
            })(
              <Select
                mode="multiple"
                style={{ width: '100%' }}
                placeholder="Please Select Beverages"
              >
                <Option key="Coffee">
                  <span>
                    <img
                      src="data:image/png;base64,"
                      alt="<coffee-icon>"
                      style={{ height: 14 }}
                    />
                  </span>{' '}
                  Coffee
                </Option>
                <Option key="Juice">
                  <span>
                    <img
                      src="data:image/png;base64,"
                      alt="<juice-icon>"
                      style={{ height: 14 }}
                    />
                  </span>{' '}
                  Juice
                </Option>
                <Option key="Cocktail">
                  <span>
                    <img
                      src="data:image/png;base64,"
                      alt="<cocktail-icon>"
                      style={{ height: 14 }}
                    />
                  </span>{' '}
                  Cocktail
                </Option>
              </Select>,
            )}
          </FormItem>
          <FormItem style={{ marginBottom: 10 }} label="Duration of Meeting">
            {getFieldDecorator('duration', {
              rules: [
                {
                  message: 'Please Select Duration of Meeting!',
                  required: true,
                },
              ],
            })(
              <Select
                mode="multiple"
                style={{ width: '100%' }}
                placeholder="Please Select Duration of Meeting"
              >
                <Option key="20-mint">20 Mint</Option>
                <Option key="60-mint">60 Mint</Option>
                <Option key="120-mint">120 Mint</Option>
              </Select>,
            )}
          </FormItem>
          <FormItem
            style={{
              bottom: 0,
              position: 'absolute',
              right: 20,
            }}
          >
            <Button
              htmlType="submit"
              type="primary"
              icon="right"
              size="large"
              loading={loading}
            >
              Last Step
            </Button>
          </FormItem>
        </Form>
      </div>
    );
  }
}

Step3.propTypes = {
  // eslint-disable-next-line
  history: PropTypes.object.isRequired,
  // eslint-disable-next-line
  form: PropTypes.object.isRequired,
};
export default Form.create()(Step3);
